import React, { useState } from 'react';
import Login from './Login';
import DocList from './DocList';
import Editor from './Editor';
import AdminPanel from './AdminPanel';

function App() {
  const [user, setUser] = useState(null);
  const [selectedDoc, setSelectedDoc] = useState(null);

  if (!user) return <Login setUser={setUser} />;

  return (
    <div>
      <div style={{ position: 'fixed', top: 0, left: 0, background: '#eee', padding: '10px 20px', width: '100%', zIndex: 1000, borderBottom: '1px solid #ccc' }}>
        <span>Welcome, <b>{user.username}</b></span>
      </div>
      <div style={{ marginTop: 50 }}>
        {user.username === 'admin' ? (
          <AdminPanel user={user} />
        ) : !selectedDoc ? (
          <DocList user={user} setSelectedDoc={setSelectedDoc} />
        ) : (
          <Editor user={user} doc={selectedDoc} goBack={() => setSelectedDoc(null)} />
        )}
      </div>
    </div>
  );
}

export default App;
