import React, { useState } from 'react';
import axios from 'axios';

export default function Login({ setUser }) {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const [mode, setMode] = useState('login'); // 'login' or 'signup'

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    try {
      if (mode === 'signup') {
        const res = await axios.post('http://localhost:4000/api/signup', { username, password });
        setUser({ username: res.data.username, user_id: res.data.user_id });
      } else {
        const res = await axios.post('http://localhost:4000/api/login', { username, password });
        setUser({ username: res.data.username, user_id: res.data.user_id });
      }
    } catch (err) {
      setError(err.response?.data?.error || (mode === 'signup' ? 'Sign up failed' : 'Login failed'));
    }
  };

  return (
    <div className="card">
      <form onSubmit={handleSubmit}>
        <h2 style={{ textAlign: 'center' }}>{mode === 'signup' ? 'Sign Up' : 'Login'}</h2>
        <div style={{ marginBottom: 16 }}>
          <input value={username} onChange={e => setUsername(e.target.value)} placeholder="Username" required style={{ width: '100%' }} />
        </div>
        <div style={{ marginBottom: 16 }}>
          <input type="password" value={password} onChange={e => setPassword(e.target.value)} placeholder="Password" required style={{ width: '100%' }} />
        </div>
        <button type="submit" style={{ width: '100%' }}>{mode === 'signup' ? 'Sign Up' : 'Login'}</button>
        <button type="button" onClick={() => { setMode(mode === 'signup' ? 'login' : 'signup'); setError(''); }} style={{ width: '100%', marginTop: 10, background: '#e0e7ef', color: '#222' }}>
          {mode === 'signup' ? 'Switch to Login' : 'Sign Up'}
        </button>
        {error && <div style={{ color: 'red', marginTop: 12, textAlign: 'center' }}>{error}</div>}
      </form>

    </div>

  );

}
