import React, { useEffect, useRef, useState } from 'react';
import axios from 'axios';
import Quill from 'quill';
import 'quill/dist/quill.snow.css';

export default function Editor({ user, doc, goBack }) {
  const editorRef = useRef();
  const quillRef = useRef();
  const [role, setRole] = useState(doc.role);
  const [shareUser, setShareUser] = useState('');
  const [shareRole, setShareRole] = useState('viewer');
  const [shareMsg, setShareMsg] = useState('');
  const [loadError, setLoadError] = useState('');
  const [collaborators, setCollaborators] = useState([]);
  const [removeMsg, setRemoveMsg] = useState('');

  // Fetch doc and collaborators
  useEffect(() => {
    axios.get(`http://localhost:4000/api/docs/${doc.id}?user_id=${user.user_id}`)
      .then(res => {
        if (quillRef.current) quillRef.current.setContents(quillRef.current.clipboard.convert(res.data.content));
        setRole(res.data.role);
        setLoadError('');
      })
      .catch(err => {
        setLoadError(err.response?.data?.error || 'Failed to load document');
      });
    // Fetch collaborators
    axios.get(`http://localhost:4000/api/docs/${doc.id}/collaborators?user_id=${user.user_id}`)
      .then(res => setCollaborators(res.data))
      .catch(() => setCollaborators([]));
  }, [doc.id, user.user_id]);

  useEffect(() => {
    quillRef.current = new Quill(editorRef.current, { theme: 'snow', readOnly: role === 'viewer' });
    const ws = new WebSocket(`ws://localhost:4000/ws/${doc.id}`);
    ws.onopen = () => {
      ws.send(JSON.stringify({ user_id: user.user_id, content: quillRef.current.root.innerHTML }));
    };
    ws.onmessage = (event) => {
      const data = JSON.parse(event.data);
      if (data.error) {
        setShareMsg(data.error);
      }
      if (data.content && data.docId === doc.id) {
        if (quillRef.current.root.innerHTML !== data.content) {
          quillRef.current.setContents(quillRef.current.clipboard.convert(data.content));
        }
      }
    };
    if (role !== 'viewer') {
      quillRef.current.on('text-change', () => {
        ws.send(JSON.stringify({ user_id: user.user_id, content: quillRef.current.root.innerHTML }));
      });
    }
    return () => {
      ws.close();
    };
    // eslint-disable-next-line
  }, [doc.id, user.user_id, role]);

  const handleShare = async (e) => {
    e.preventDefault();
    setShareMsg('');
    try {
      await axios.post(`http://localhost:4000/api/docs/${doc.id}/share`, {
        username: shareUser,
        role: shareRole,
        user_id: user.user_id
      });
      setShareMsg('Shared!');
      setShareUser('');
      // Refresh collaborators
      const res = await axios.get(`http://localhost:4000/api/docs/${doc.id}/collaborators?user_id=${user.user_id}`);
      setCollaborators(res.data);
    } catch (err) {
      setShareMsg(err.response?.data?.error || 'Share failed');
    }
  };

  const handleRemove = async (username) => {
    setRemoveMsg('');
    try {
      await axios.post(`http://localhost:4000/api/docs/${doc.id}/remove`, {
        username,
        user_id: user.user_id
      });
      setRemoveMsg('Removed!');
      // Refresh collaborators
      const res = await axios.get(`http://localhost:4000/api/docs/${doc.id}/collaborators?user_id=${user.user_id}`);
      setCollaborators(res.data);
    } catch (err) {
      setRemoveMsg(err.response?.data?.error || 'Remove failed');
    }
  };

  return (
    <div style={{ margin: 40 }}>
      <button onClick={goBack}>Back</button>
      <h2>{doc.title} ({role})</h2>
      {loadError && <div style={{ color: 'red', marginBottom: 10 }}>{loadError}</div>}
      <div ref={editorRef} style={{ height: 300, marginBottom: 20 }} />
      {role === 'owner' && (
        <div style={{ marginTop: 20 }}>
          <h4>Collaborators</h4>
          <ul>
            {collaborators.filter(c => c.username !== user.username).map(c => (
              <li key={c.username}>
                {c.username} ({c.role})
                <button style={{ marginLeft: 10 }} onClick={() => handleRemove(c.username)}>Remove</button>
              </li>
            ))}
          </ul>
          {removeMsg && <div style={{ color: removeMsg === 'Removed!' ? 'green' : 'red' }}>{removeMsg}</div>}
        </div>
      )}
      {role === 'owner' ? (
        <form onSubmit={handleShare} style={{ marginTop: 20 }}>
          <h4>Share with user</h4>
          <input value={shareUser} onChange={e => setShareUser(e.target.value)} placeholder="Username" required />
          <select value={shareRole} onChange={e => setShareRole(e.target.value)}>
            <option value="editor">Editor</option>
            <option value="viewer">Viewer</option>
          </select>
          <button type="submit">Share</button>
          {shareMsg && <span style={{ marginLeft: 10, color: shareMsg === 'Shared!' ? 'green' : 'red' }}>{shareMsg}</span>}
        </form>
      ) : (
        <div style={{ marginTop: 20, color: '#888' }}>
          Only the owner can invite people to this document.
        </div>
      )}
    </div>
  );
}
