import React, { useState } from 'react';
import axios from 'axios';

export default function Login({ setUser }) {
  const [username, setUsername] = useState('');
  const [error, setError] = useState('');
  const [mode, setMode] = useState('login'); // 'login' or 'signup'

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.post('http://localhost:4000/api/login', { username });
      setUser({ username: res.data.username, user_id: res.data.user_id });
    } catch (err) {
      setError(mode === 'signup' ? 'Sign up failed' : 'Login failed');
    }
  };

  return (
    <form onSubmit={handleSubmit} style={{ margin: 40 }}>
      <h2>{mode === 'signup' ? 'Sign Up' : 'Login'}</h2>
      <input value={username} onChange={e => setUsername(e.target.value)} placeholder="Username" required />
      <button type="submit">{mode === 'signup' ? 'Sign Up' : 'Login'}</button>
      <button type="button" onClick={() => { setMode(mode === 'signup' ? 'login' : 'signup'); setError(''); }} style={{ marginLeft: 10 }}>
        {mode === 'signup' ? 'Switch to Login' : 'Sign Up'}
      </button>
      {error && <div style={{ color: 'red' }}>{error}</div>}
    </form>
  );
}
