import React, { useEffect, useState } from 'react';
import axios from 'axios';

export default function DocList({ user, setSelectedDoc }) {
  const [docs, setDocs] = useState([]);
  const [title, setTitle] = useState('');

  useEffect(() => {
    axios.get(`http://localhost:4000/api/docs?user_id=${user.user_id}`)
      .then(res => setDocs(res.data));
  }, [user]);

  const createDoc = async (e) => {
    e.preventDefault();
    const res = await axios.post('http://localhost:4000/api/docs', { title, user_id: user.user_id });
    setDocs([...docs, res.data]);
    setTitle('');
  };

  return (
    <div style={{ margin: 40 }}>
      <h2>Your Documents</h2>
      <form onSubmit={createDoc}>
        <input value={title} onChange={e => setTitle(e.target.value)} placeholder="New doc title" required />
        <button type="submit">Create</button>
      </form>
      <ul>
        {docs.map(doc => (
          <li key={doc.id}>
            <button onClick={() => setSelectedDoc(doc)}>{doc.title} ({doc.role})</button>
          </li>
        ))}
      </ul>
    </div>
  );
}
