import React, { useState } from 'react';
import axios from 'axios';

export default function Settings({ user, onClose, onThemeChange, theme }) {
  const [oldPassword, setOldPassword] = useState('');
  const [newPassword, setNewPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');
  const [msg, setMsg] = useState('');
  const [error, setError] = useState('');

  const handlePasswordChange = async (e) => {
    e.preventDefault();
    setMsg('');
    setError('');
    if (newPassword !== confirmPassword) {
      setError('New passwords do not match.');
      return;
    }
    try {
      await axios.post('http://localhost:4000/api/change_password', {
        username: user.username,
        old_password: oldPassword,
        new_password: newPassword
      });
      setMsg('Password changed successfully!');
      setOldPassword('');
      setNewPassword('');
      setConfirmPassword('');
    } catch (err) {
      setError(err.response?.data?.error || 'Password change failed');
    }
  };

  return (
    <div className="card" style={{ maxWidth: 400, margin: '40px auto', padding: 24, position: 'relative' }}>
      <button onClick={onClose} style={{ position: 'absolute', top: 10, right: 10, background: 'none', border: 'none', fontSize: 20, cursor: 'pointer' }}>&times;</button>
      <h2 style={{ textAlign: 'center', marginBottom: 24 }}>Settings</h2>
      <form onSubmit={handlePasswordChange}>
        <div style={{ marginBottom: 16 }}>
          <input type="password" value={oldPassword} onChange={e => setOldPassword(e.target.value)} placeholder="Current Password" required style={{ width: '100%' }} />
        </div>
        <div style={{ marginBottom: 16 }}>
          <input type="password" value={newPassword} onChange={e => setNewPassword(e.target.value)} placeholder="New Password" required style={{ width: '100%' }} />
        </div>
        <div style={{ marginBottom: 16 }}>
          <input type="password" value={confirmPassword} onChange={e => setConfirmPassword(e.target.value)} placeholder="Confirm New Password" required style={{ width: '100%' }} />
        </div>
        <button type="submit" style={{ width: '100%' }}>Change Password</button>
        {msg && <div style={{ color: 'green', marginTop: 12, textAlign: 'center' }}>{msg}</div>}
        {error && <div style={{ color: 'red', marginTop: 12, textAlign: 'center' }}>{error}</div>}
      </form>
      <hr style={{ margin: '32px 0' }} />
      <div style={{ textAlign: 'center' }}>
        <span>Theme: </span>
        <button onClick={() => onThemeChange(theme === 'light' ? 'dark' : 'light')} style={{ marginLeft: 8 }}>
          {theme === 'light' ? 'Switch to Dark Mode' : 'Switch to Light Mode'}
        </button>
      </div>
    </div>
  );
}
