import React, { useState } from 'react';
import axios from 'axios';

export default function AdminPanel({ user }) {
  const [target, setTarget] = useState('');
  const [adminPass, setAdminPass] = useState('');
  const [msg, setMsg] = useState('');

  const handleDelete = async (e) => {
    e.preventDefault();
    setMsg('');
    try {
      await axios.delete('http://localhost:4000/api/admin/delete_user', {
        params: {
          username: target,
          admin_user: user.username,
          admin_pass: adminPass
        }
      });
      setMsg('User deleted!');
    } catch (err) {
      setMsg(err.response?.data?.error || 'Delete failed');
    }
  };

  return (
    <div style={{ margin: 40, border: '1px solid #aaa', padding: 20, background: '#f9f9f9' }}>
      <h2>Admin Panel</h2>
      <form onSubmit={handleDelete}>
        <input value={target} onChange={e => setTarget(e.target.value)} placeholder="Username to delete" required />
        <input type="password" value={adminPass} onChange={e => setAdminPass(e.target.value)} placeholder="Admin password" required />
        <button type="submit">Delete User</button>
      </form>
      {msg && <div style={{ color: msg === 'User deleted!' ? 'green' : 'red', marginTop: 10 }}>{msg}</div>}
    </div>
  );
}
